import React, { Component,props } from "react";
import MorePopover from "./MorePopover"
class View extends Component {
	constructor() {
    super(props);

    this.state = {
      showContextMenu:false
    };
}
    onContextMenuClick =(e)=>{
      e.preventDefault();
      const x = e.clientX;
      const y = e.clientY;
      this.setState({
        showContextMenu: {
          screenX: x,
          screenY: y
        }
      });
    }
 closeContextMenu = () => {
    
    this.setState({ showContextMenu: false });
  };
  render() {
    return (
      <div name="View" onContextMenu={(e) => this.onContextMenuClick(e)}>
       <span>{this.props.selected}</span>
       <MorePopover
          ref={(morePopNode) => (this._morePopup = morePopNode)}
          asset={this.activeContext}
          permission={{ canShowSpotLightAction: false }}
          onDeleteSuccess={this._assetListBlockRef.onDeleteSuccess}
          onMenuClose={this.onMenuClose}
          onMenuOpen={this.onMenuOpen}
          onRenameSuccess={this._assetListBlockRef.onRenameSuccess}
          showPopover={showContextMenu}
          closeContextMenu={this.closeContextMenu}
          downloadAsset={this._assetListBlockRef.downloadSingleAsset}
        />
        </div>
    );
  }
}

export default View;
