import React, { Component,props } from "react";
import "./Treeview.css"

class TreeView extends Component {
	constructor() {
    super(props);

    this.state = {
      
    };
}
    onClick = (e)=>{
    	e.target.classList.add('selected');
    	this.props.setSelected(e.target.getAttribute('value'))
    }

  render() {
  	const {folders} = this.props;
    return (
      <div name="TreeView">
      <div className="fav">Favourites
      {folders.map((folder)=>{
      	return <span className={this.props.selected === folder ? 'selected':null} onClick ={this.onClick} value={folder}> {folder}</span>
      })}
      
       </div>
       <div className="fav">Icloud
      <span onClick ={this.onClick}> icloud Drive</span>
      <span onClick ={this.onClick}> Documents </span>
       <span onClick ={this.onClick}> Desktop </span>

       </div>
        </div>
    );
  }
}

export default TreeView;
