import React, { Component,props } from "react";
import MorePopover from "./MorePopover"
class View extends Component {
	constructor() {
    super(props);

    this.state = {
      showContextMenu:false
    };
}
    onContextMenuClick =(e)=>{
      e.preventDefault();
      const x = e.clientX;
      const y = e.clientY;
      this.setState({
        showContextMenu: {
          screenX: x,
          screenY: y
        }
      });
    }
 closeContextMenu = () => {
    
    this.setState({ showContextMenu: false });
  };
  onDelete = ()=>{
    console.log('onDelete')
    this.props.onDelete(this.props.selected);
    this.closeContextMenu()
  }
  onCreate=()=>{
this.props.onCreate();
  }
  render() {
    return (
      <div name="View" onContextMenu={(e) => this.onContextMenuClick(e)} >
       <span>{this.props.selected}</span>
       <MorePopover
          ref={(morePopNode) => (this._morePopup = morePopNode)}
          showPopover={this.state.showContextMenu}
          closeContextMenu={this.closeContextMenu}
          onCreate={this.onCreate}
          onDelete={this.onDelete}
        />
        </div>
    );
  }
}

export default View;
